import '../assets/PantallaLogin.css'

class Recomendacion extends Component{
    render(){
        return(
        <div className="modalRecomenda">
            <div className="row">
                <p className="textoMediano">{this.props.titulo}</p>
            </div>
            <div className="row">
                <p className="textoNormal">{this.props.descri}</p>
            </div>
            <div className="row">
                <img className="imageRecomenda" style = "background-image: url(./img/lamina1.jpg);"        ></img>
            </div>

        </div>  
        );
    }
}
export default Recomendacion;
