import React, { Component } from  'react';
import '../assets/PantallaLogin.css';
import Image from 'react-bootstrap/Image';
import { Slide } from 'react-slideshow-image';



class Recomendacion extends Component{
    source = "";
    slideImages = [
        this.props.image1,
        this.props.image2,
      ];
    render(){
        return(
        <div className="modalRecomenda">
            <div className="row">
                <p className="textoMediano">{this.props.titulo}</p>

            </div>
            <div className="row">
                <p className="textoNormal">{this.props.descri}</p>
            </div>
            <div className="row">
                <Image className="imageRecomenda" src= {this.props.image1} ></Image>
                <div className="slide-container">
                    <Slide>
                        <div className="each-slide">
                            <div style={{'backgroundImage': `url(${slideImages[0]})`}}>
                            <span>Slide 1</span>
                            </div>
                        </div>
                        <div className="each-slide">
                            <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
                            <span>Slide 2</span>
                            </div>
                        </div>

                    </Slide>
                </div>
            </div>
        </div>  
        );
    }
    ponerDato = () =>
    {
        this.source = "'"+ this.props.image + "'";
    }
}
export default Recomendacion;
